                <textarea className="col-md-6 pt-2" onKeyPress={this.handleKeyPressed} onChange={this.handleChange} value={this.state.textareaVal} />
                <button className="col-md-1" onClick={this.handleClick}>Отправить</button>
            </div>
        );
    }
}

export default ChatInput;
