import React from 'react';

class UserOnline extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            toggleMenu: false
        }
    }

    toggleMenu = (event) => {
        this.setState({toggleMenu: !this.state.toggleMenu})
    };

    render() {

        const divStyle = {
            position: 'absolute',
            display: this.state.toggleMenu ? 'block': 'none',
            border: 'black 1px solid',
            minWidth: '160px',
            boxShadow: '0px 8px 16px 0px rgba(0,0,0,0.2)',
            backgroundColor: 'white',
            zIndex: 1
        };


        return (
            <div>
                <img src={this.props.user.avatar} style={{cursor: 'pointer'}} onClick={this.toggleMenu} />{this.props.user.nickname}
                <div style={divStyle}>
                    TEST
                </div>
            </div>
        )
    }
}

export default UserOnline;
