import React from 'react';
import Messages, {updateMessage} from "./Messages";
import ChatInput from "./ChatInput";
import Login from './Login';

let connection = null;
function createWebsocketConnection() {
    connection = new WebSocket('ws://127.0.0.1:5000');
    connection.onopen = function() {
        console.log('HELLO');
    };
    connection.onerror = function (error) {
        console.log(error);
    };
    connection.onmessage = function (message) {
        updateMessages(message.data);
    };
}
function send() {
    connection.send(document.querySelector('textarea').value);
    document.querySelector('textarea').value = '';
}
function updateMessages(message) {
    this.setState({message: this.state.message.concat(<h2>{message}</h2>)});
}

class Chat extends React.Component{

    callBackendAPI = async () => {
        const response = await fetch('/index');
        const body = await response.json();

        if (response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    };

    render() {
        return (
            <div>
                <Login />
                <Messages updateMessagesFunction={updateMessages} />
                <ChatInput sendFunction={send} />
            </div>);
    }
}

export default Chat;
export {createWebsocketConnection};
