import React, { Component } from 'react';
import './App.css';
import ReactDOM from 'react-dom';

class ChatInput extends Component {

    componentDidMount() {
        // Call our fetch function below once the component mounts
        this.callBackendAPI()
            .then(res => this.setState({ data: res.express }))
            .catch(err => console.log(err));
    }
    // Fetches our GET route from the Express server. (Note the route we are fetching matches the GET route from server.js
    callBackendAPI = async () => {
        const response = await fetch('/index');
        const body = await response.json();

        if (response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    };

    setWebsocket = () => {
        const connection = new WebSocket('ws://127.0.0.1:5000');
        connection.onopen = function(){
            console.log('HELLO');
        };
        connection.onerror = function (error) {
            console.log(error);
        };
        connection.onmessage = function (message) {
            function Print(props) {
                return <p>{props.text}</p>;
            }
            ReactDOM.render(<Print text={message}/>, document.querySelector('body'));
        };
        return connection;
    };

    connection = this.setWebsocket();

    // sendMessage = (message) => {
    //     this.connection.send(message);
    // };

    render() {
        return (
            <div className="row d-flex justify-content-center mb-5 fixed-bottom">
                <textarea className="col-md-6 pt-2"></textarea>
                <button className="col-md-1" onClick={() => this.connection.send(document.querySelector('textarea').value)
                    }>Отправить</button>
            </div>
        );
    }
}

export default ChatInput;
