import React from 'react';
import Messages from "./Messages";
import ChatInput from "./ChatInput";
import Login from './Login';
import openSocket from 'socket.io-client';
import UserOnline from "./UserOnline";
import {BrowserRouter as Router, Route, Switch, Redirect, Link} from "react-router-dom";
import Private from './Private/Private';
// import Cookies from "universal-cookie";

class Chat extends React.Component{

    constructor(props) {
        super(props);
        this.pathToPrivateChat = '/private/';
        this.conversationId = null;
        // fetch('/session/user').then(res => res.json()).then(data => {
        //     console.log('ID', data.user.id);
        //     this.conversationId = data.user.id;
        // });
        const getSessionId = async () => {
            const response = await fetch('/session/user');
            const data = await response.json();
            this.conversationId = data.user.id
        };
        getSessionId();
        this.socket = null;
        this.state = {
            usersOnline: [],
            messages: [],
        };
    }

    componentDidMount() {
    };

    createRoom = (nickname) => {
        this.socket = openSocket('http://localhost:5000/room?nickname=' + nickname);
        this.socket.on('userConnected', (message) => {
            console.log('Websocket: broadcast from \'room\' namespace \'connected\' channel:', message);
            this.updateUsersOnline(message.data.usersOnline);
            this.updateMessages(message);
        });
        this.socket.on('message', (message) => {
            console.log('Websocket: broadcast from \'room\' namespace \'message\' channel:', message);
            this.updateMessages(message);
        });
        this.socket.on('userDisconnected', (message) => {
            console.log('Websocket: broadcast from \'room\' namespace \'userDisconnected\' channel:', message);
            this.updateUsersOnline(message.data.usersOnline);
            this.updateMessages(message);
        });
    };

    createPrivate = (personId) => {
        this.conversationId += ('-' + personId);
        console.log(this.conversationId);
        this.socket = openSocket(`http://localhost:5000/private?conversationId=${this.conversationId}`);
        this.socket.on('established', (message) => {
            console.log('Websocket: broadcast from \'private\' namespace \'established\' channel:', message);
            // this.updateUsersOnline(message.data.usersOnline);
            this.updateMessages(message);
        });
        this.socket.on('message', (message) => {
            console.log('Websocket: broadcast from \'private\' namespace \'message\' channel:', message);
            this.updateMessages(message);
        });
        // this.socket.on('demolished', (message) => {
        //     console.log('Websocket: broadcast from \'private\' namespace \'userDisconnected\' channel:', message);
        //     this.updateUsersOnline(message.data.usersOnline);
        //     this.updateMessages(message);
        // });
    };

    sendToServer = (message) => {
        this.socket.emit('message', message);
    };

    updateMessages = (message) => {
        this.setState({messages: this.state.messages.concat(message)});
    };

    updateUsersOnline = (users) => {
        this.setState({usersOnline: users});
    };

    render() {
        const usersOnlineStyle = {
            float: 'right',
            width: '400px',
            height: '600px',
            border: 'black 2px solid',
            padding: '20px'
        };
        const usersOnline = this.state.usersOnline.map((user, i) => {
            return <UserOnline key={i} user={user} path={this.pathToPrivateChat} />
        });
        return(
            <div>
                <Router>
                    <Switch>
                        <Route path={`${this.pathToPrivateChat}:id`}
                               render={(props) => <Private {...props} createPrivate={this.createPrivate}/>}/>
                        {/*<Redirect from={'/:path'} to={'/'} />*/}
                        <Route path={'/'} render={() => <Login createRoom={this.createRoom} />} />
                    </Switch>
                </Router>
                <div style={usersOnlineStyle}>
                    {usersOnline}
                </div>
                <Messages messages={this.state.messages} />
                <ChatInput sendToServer={this.sendToServer} />
            </div>
        );
    }
}

export default Chat;
