import React from 'react';
import Messages from "./Messages";
import ChatInput from "./ChatInput";
import Login from './Login';
// import {updateMessages} from "./Messages";

class Chat extends React.Component{

    constructor(props) {
        super(props);
        // this.updateMessages = updateMessages.bind(this);
        // this.updateMessages = updateMessages.bind(this);
        // console.log(this.updateMessages);
    }

    connection = null;

    createWS = () => {
        this.connection = new WebSocket('ws://127.0.0.1:5000');
        this.connection.onopen = function() {
            console.log('HELLO');
        };
        this.connection.onerror = function (error) {
            console.log(error);
        };
        this.connection.onmessage = function (message) {
            console.log(this);
            this.updateMessages(message.data);
        };
        // return connection;
    };

    updateMessages = (message) => {
        this.setState({message: this.state.message.concat(<h2>{message}</h2>)});
    };

    sendToServer = () => {
        this.connection.send(document.querySelector('textarea').value);
        document.querySelector('textarea').value = '';
    };


    callBackendAPI = async () => {
        const response = await fetch('/index');
        const body = await response.json();

        if (response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    };

    render() {
        return (
            <div>
                <Login createWS={this.createWS}/>
                <Messages updateMessages={this.updateMessages}  />
                <ChatInput sendFunction={this.sendToServer} />
            </div>);
    }
}

export default Chat;
// export {updateMessages};
