import React from 'react';
import {updateMessage} from './Messages';


class ChatInput extends React.Component {

    // componentDidMount() {
    //     this.callBackendAPI()
    //         .then(res => this.setState({ data: res.express }))
    //         .catch(err => console.log(err));
    // }

    constructor(props) {
        super(props);
        this.sendFunction = this.props.sendFunction;
    }

    render() {
        return (
            <div className="row d-flex justify-content-center mb-5 fixed-bottom">
                <textarea className="col-md-6 pt-2" />
                <button className="col-md-1" onClick={this.sendFunction}>Отправить</button>
            </div>
        );
    }
}

export default ChatInput;
