import React from 'react';

class UserOnline extends React.Component{

    constructor(props) {
        super(props);
        this.aRef = React.createRef();
        this.divRefs = [];
        this.state = {
            toggleMenu: false,
            subDivBackground: 'white'
        }
    }

    componentDidMount() {
        document.addEventListener('click', this.handleOutsideClick);
        for(let divRef of this.divRefs) {
            divRef.addEventListener('mouseenter', this.handleMouseEnter);
            divRef.addEventListener('mouseleave', this.handleMouseLeave);
        }
    }

    toggleMenu = () => {
        this.setState({toggleMenu: !this.state.toggleMenu});
    };


    handleOutsideClick = (event) => {
        if ((this.aRef.current !== event.target) && (this.aRef.current !== event.target.parentElement)){
            this.setState({toggleMenu: false});
        }
    };

    handleMouseEnter = () => {
        this.setState({subDivBackground: 'blue'});
    };

    handleMouseLeave = () => {
        this.setState({subDivBackground: 'white'});
    };

    render() {
        const subDivStyle = {
            backgroundColor: this.state.subDivBackground
        };
        const divStyle = {
            position: 'absolute',
            display: this.state.toggleMenu ? 'block': 'none',
            border: 'black 1px solid',
            minWidth: '160px',
            boxShadow: '0px 8px 16px 0px rgba(0,0,0,0.2)',
            backgroundColor: 'white',
            zIndex: 1
        };

        return (
            <a ref={this.aRef} onClick={this.toggleMenu}>
                <img src={this.props.user.avatar} style={{cursor: 'pointer'}} onClick={this.toggleMenu} />{this.props.user.nickname}
                <div style={divStyle}>
                    <div ref={elem => this.divRefs.push(elem)} style={subDivStyle}>Написать в ЛС</div>
                    <div ref={elem => this.divRefs.push(elem)} style={subDivStyle}>TEST</div>
                </div>
            </a>
        )
    }
}

export default UserOnline;
