import React from 'react';

class ChatInput extends React.Component {

    // componentDidMount() {
    //     this.callBackendAPI()
    //         .then(res => this.setState({ data: res.express }))
    //         .catch(err => console.log(err));
    // }

    handleKeyPressed = (event) => {
        if (event.key !== 'Enter') return false;
        this.handleClick();
    };

    handleClick = () => {
        this.props.sendToServer();
        this.refs.textarea.focus();
        this.refs.textarea.value = '';
    };

    render() {
        return (
            <div className="row d-flex justify-content-center mb-5 fixed-bottom">
                <textarea className="col-md-6 pt-2" ref='textarea' onKeyPress={this.handleKeyPressed} />
                <button className="col-md-1" onClick={this.handleClick}>Отправить</button>
            </div>
        );
    }
}

export default ChatInput;
