import React from 'react';
import '../css/styles.css';
import {chatInputRef} from "./ChatInput";
import ReactDOM from 'react-dom';

class Messages extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            height: '100%'
        }
    }

    componentDidMount() {
        console.log(chatInputRef);
        // this.setState({height: ReactDOM.findDOMNode(chatInputRef).getBoundingClientRect().top})
    }

    render() {

        const info = {
            color: '#828282',
            fontStyle: 'italic',
            display: 'flex',
            justifyContent: 'center',
            padding: '0px'
        };

        const messagesDiv = {
            border: '1px black solid',
            overflow: 'scroll',
            overflowX: 'hidden',
            height: this.state.height
        };

        const messages = this.props.messages.map((message, i) => {

            const messageStyle = {
                fontSize: '24px',
                padding: '5px',
                wordWrap: 'break-word'
            };

            if (message.type === 'info') Object.assign(messageStyle, info);
            return <div style={messageStyle} key={i}>
                {message.text}
            </div>
        });
        return <div style={messagesDiv}>{messages}</div>;
    }
}

export default Messages;
