import React, { Component } from 'react';
import '../css/styles.css';

class Messages extends Component {

    render() {

        const info = {
            color: '#828282',
            fontStyle: 'italic',
            justifyContent: 'center'
        };

        const messages = this.props.messages.map((message) => {

            const messageStyle = {
                fontSize: '24px',
                padding: '10px',
                display: 'flex'
            };

            if (message.type === 'info') Object.assign(messageStyle, info);
            return <div style={messageStyle}>
                {message.text}
            </div>
        });
        return <div style={{display: 'inline-block'}}>{messages}</div>;
    }
}

export default Messages;
