import React from 'react';
import Messages, {updateMessage} from "./Messages";
import ChatInput from "./ChatInput";
import Login from './Login';

class Chat extends React.Component{

    createWebsocketConnection = () => {
        const connection = new WebSocket('ws://127.0.0.1:5000');
        connection.onopen = function() {
            console.log('HELLO');
        };
        connection.onerror = function (error) {
            console.log(error);
        };
        connection.onmessage = function (message) {
            updateMessage(message.data);
        };
        return connection;
    };

    connection = this.createWebsocketConnection();

    send = () => {
        this.connection.send(document.querySelector('textarea').value);
        document.querySelector('textarea').value = '';
    };

    render() {
        return (
            <div>
                <Login />
                <Messages />
                <ChatInput sendFunction={this.send} />
            </div>);
    }
}

export default Chat;
