import React from 'react';
import {Modal, ModalBody, ModalHeader, ModalFooter, Button} from 'reactstrap';
import '../css/styles.css';

class Login extends React.Component {

    constructor(props){
        super(props);
        this.inputVal = null;
        this.state = {
            isOpen: true
        };
    }

    updateInputVal = (inputVal) => {
        alert(inputVal);
        this.inputVal = inputVal;
    };

    handleClick = () => {
        alert(this.inputVal.value);
        this.setState({isOpen: false})
    };

    render() {
        return (
            <Modal id="login" isOpen={this.state.isOpen} centered>
                <ModalHeader className='flex-centered'>
                    <h5>Введите свой никнейм</h5>
                </ModalHeader>
                <ModalBody className='flex-centered'>
                    <input className='match-parent' value={this.inputVal} ref={(input) => this.inputVal = input} />
                </ModalBody>
                <ModalFooter>
                    <Button onClick={this.handleClick}>ОК</Button>
                </ModalFooter>
            </Modal>
        );
    }
}

export default Login;
