import React from 'react';
import ReactDOM from 'react-dom';
import About from './About';
import Chat, {updateNav} from './Chat/Chat';
import Private, {pathToPrivateChat} from './Chat/Private';
import {BrowserRouter as Router, Route, Switch, Redirect} from 'react-router-dom';
import openSocket from 'socket.io-client';


function connectToPrivate(partner) {
    fetch('/session/user').then(res => res.json()).then(data => {
        console.log(data);
        let conversationId = (data.user.id + '-' + partner.id);
        socket = openSocket(`http://localhost:5000/private?conversationId=${conversationId}`);
        socket.on('established', (message) => {
            console.log('Websocket: broadcast from \'private\' namespace \'established\' channel:', message);
            // this.updateUsersOnline(message.data.usersOnline);
            updateNav({
                navName: partner.nickname,
                route: pathToPrivateChat+partner.nickname
            });
            this.setState({messages: this.state.messages.concat(message)});
        });
        socket.on('message', (message) => {
            console.log('Websocket: broadcast from \'private\' namespace \'message\' channel:', message);
            this.setState({messages: this.state.messages.concat(message)});
        });
        // this.socket.on('demolished', (message) => {
        //     console.log('Websocket: broadcast from \'private\' namespace \'userDisconnected\' channel:', message);
        //     this.updateUsersOnline(message.data.usersOnline);
        //     this.updateMessages(message);
        // });
    });
}
function sendToServer(message) {
    socket.emit('message', message);
}

ReactDOM.render(
    <Router>
        <Switch>
            <Route path="/" component={Chat}/>
            <Route path="/about" component={About}/>
            <Redirect to={'/'} />
        </Switch>
    </Router>
    , document.getElementById('root'));


export {connectToWS, sendToServer, connectToPrivate};
