import React from 'react';
import {Modal, ModalBody, ModalHeader, ModalFooter} from 'reactstrap';

class Login extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            display: 'block'
        };
    }

    hideModal = () => {
        this.state.display = 'none'
    };

    render() {
        return (
            <div id="login" className={Modal} style={{display: this.state.display}}>
                <div>
                    <div className="modal-content">
                        <div className={ModalHeader}>
                            <h5 className="modal-title">Введите свой никнейм</h5>
                            <button type="button" className="close" onClick={this.hideModal}>
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div className={ModalBody}>
                            <input />
                        </div>
                        <div className={ModalFooter}>
                            <button type="button" className="btn btn-primary" onClick={this.hideModal}>ОК</button>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Login;
