import React from 'react';
import Messages from "./Messages";
import ChatInput from "./ChatInput";
import Login from './Login';
import UserOnline from "./UserOnline";
import {BrowserRouter as Router, Route, Switch, Redirect, Link} from "react-router-dom";
import openSocket from 'socket.io-client';

const ROOM_NAV_NAME = 'ROOM';
const ROOM_MESSAGE_STORE = 'ROOM';

class Chat extends React.Component{

    constructor(props) {
        super(props);
        this.roomMessages = [];
        this.privateMessages = {};
        this.state = {
            sockets: [],
            currentSocket: null,
            nav: [],
            usersOnline: [],
            messages: [],
        };
    }

    connectToRoom = (nickname) => {
        const socket = openSocket('http://localhost:5000?nickname=' + nickname);
        socket.on('userConnected', (message) => {
            console.log('Websocket: broadcast from \'room\' namespace \'connected\' channel:', message);
            this.setState({usersOnline: message.data.usersOnline});
            this.roomMessages.push(message);
            this.setState({
                messages: this.roomMessages,
                sockets: this.state.sockets.concat(socket),
                currentSocket: socket,
            });
            this.updateNav({
                navName: ROOM_NAV_NAME,
                messagesStore: ROOM_MESSAGE_STORE,
                socket,
            });
        });
        socket.on('message', (message) => {
            console.log('Websocket: broadcast from \'room\' namespace \'message\' channel:', message);
            this.roomMessages.push(message);
            this.setState({
                messages: this.roomMessages,
            });
        });
        socket.on('join', (message) => {
            console.log('Websocket: broadcast from \'join\' channel:', message);
            // this.updateUsersOnline(message.data.usersOnline);
            let partner = {
                id: message.data.conversationId.split('-')[1]
            };
            this.connectToPrivate(partner)
        });
        socket.on('userDisconnected', (message) => {
            console.log('Websocket: broadcast from \'userDisconnected\' channel:', message);
            this.setState({usersOnline: message.data.usersOnline});
            this.roomMessages.push(message);
            this.setState({
                messages: this.roomMessages,
            });
        });
    };
    connectToPrivate = (partner) => {
        fetch('/session/user').then(res => res.json()).then(data => {
            console.log('PRIVATE');
            const conversationId = (data.user.id + '-' + partner.id);
            const socket = openSocket(`http://localhost:5000/?conversationId=${conversationId}`);
            socket.on('established', (message) => {
                console.log('Websocket: broadcast \'established\' channel:', message);
                // this.updateUsersOnline(message.data.usersOnline);
                this.updateNav({
                    navName: partner.nickname,
                    messagesStore: conversationId,
                    socket,
                });
                if (!this.privateMessages[conversationId])
                    this.privateMessages[conversationId] = [];
                this.privateMessages[conversationId].push(message);
                this.setState({
                    messages: this.privateMessages[conversationId],
                    currentSocket: socket
                });
            });
            socket.on('message', (message) => {
                console.log('Websocket: broadcast from \'private\' namespace \'message\' channel:', message);
                if (!this.privateMessages[conversationId])
                    this.privateMessages[conversationId] = [];
                this.privateMessages[conversationId].push(message);
                this.setState({
                    messages: this.privateMessages[conversationId],
                });
            });
            // this.socket.on('demolished', (message) => {
            //     console.log('Websocket: broadcast from \'private\' namespace \'userDisconnected\' channel:', message);
            //     this.updateUsersOnline(message.data.usersOnline);
            //     this.updateMessages(message);
            // });
        });
    };
    sendToServer =(message) => {
        this.state.currentSocket.emit('message', message);
    };
    updateNav = (nav) => {
        for (let navSaved of this.state.nav) {
            if (navSaved.messagesStore === nav.messagesStore)
                return;
        }
        this.setState({nav: this.state.nav.concat(nav)})
    };

    render() {
        console.log('NAV', this.state.nav);
        console.log('CHAT', this.state.messages);
        const nav = this.state.nav.map((item, i) => {
            return <Link to={'#'} className="nav-item nav-link" key={i}
                         onClick={()=>{
                             if (item.messagesStore === ROOM_MESSAGE_STORE)
                                 this.setState({
                                     messages: this.roomMessages
                                 });
                             else this.setState({
                                 messages: this.privateMessages[item.messagesStore]
                             });
                             this.setState({currentSocket: item.socket})
                         }}>{item.navName}</Link>
        });
        const usersOnline = this.state.usersOnline.map((user, i) => {
            return <UserOnline key={i} user={user}
                               connectToPrivate={this.connectToPrivate} />
        });
        return(
            <div>
                <nav className="navbar navbar-expand-lg navbar-light bg-light">
                    <a className="navbar-brand" href="#">
                        <h3>Chat</h3>
                    </a>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup"
                            aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon" />
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
                        <div className="navbar-nav">
                            {nav}
                        </div>
                    </div>
                </nav>
                <Login connectToRoom={this.connectToRoom}/>
                <div className='usersOnlineStyle'>
                    {usersOnline}
                </div>
                <Messages messages={this.state.messages}/>
                <ChatInput sendToServer={this.sendToServer} />
            </div>
        );
    }
}

export default Chat;
