import React from 'react';
import Messages from "./Messages";
import ChatInput from "./ChatInput";
import Login from './Login';
import UsersOnline from './UsersOnline';

class Chat extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            usersOnline: [],
            messages: []
        };
    }

    connection = null;

    createWS = (connectionID) => {
        this.connection = new WebSocket('ws://127.0.0.1:5000/?connectionID=' + connectionID);
        this.connection.onopen = () => {
            console.log('WEBSOCKET RUN');
            this.updateUsersOnline()
                .then(data => this.setState( {usersOnline: data}));
            this.updateChat({
                text: 'Пользователь ' + connectionID + ' присоединился к чату',
                type: 'info'
            });
        };
        this.connection.onmessage = (message) => {
            console.log(message);
            let messageJSON = JSON.parse(message.data);
            console.log(messageJSON);
            this.updateChat({
                        text: messageJSON.data,
                        type: ''
                    });
        };
        // this.connection.onclose = () => {
        //
        // };
    };

    sendToServer = (message) => {
        this.connection.send(message);
    };

    updateChat = (message) => {
        this.setState({messages: this.state.messages.concat(message)});
    };

    updateUsersOnline = async () => {
        const response = await fetch('/users-online');
        const body = await response.json();
        if (response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    };

    render() {
        return (
            <div>
                <Login createWS={this.createWS} />
                <div>
                    <UsersOnline usersOnline={this.state.usersOnline}/>
                    <Messages messages={this.state.messages} />
                </div>
                <ChatInput sendToServer={this.sendToServer} />
            </div>
        );
    }
}

export default Chat;
