import React from 'react';
import Messages from "./Messages";
import ChatInput from "./ChatInput";
import Login from './Login';
import UsersOnline from './UsersOnline';
import Message from '../models/message';

class Chat extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            usersOnline: [],
            messages: []
        };
    }

    connection = null;

    createWS = (nickname) => {
        this.connection = new WebSocket('ws://127.0.0.1:5000/?nickname=' + nickname);
        this.connection.onopen = () => {
            console.log('WEBSOCKET RUN');
            this.apiUsersOnline(nickname)
                .then(data => {
                    let dataJSON = JSON.parse(data);
                });
        };
        this.connection.onmessage = (message) => {
            let messageJSON = JSON.parse(message.data);
            switch (messageJSON.channel) {
                case 'usersOnline':
                    this.updateUsersOnline(messageJSON.data);
                    this.updateMessages(new Message(messageJSON.message.text,
                        messageJSON.message.type));
                    break;
                case 'messages':
                    this.updateMessages(new Message(messageJSON.message.text,
                        messageJSON.message.type));
                    break;
            }
        };
    };

    sendToServer = (message) => {
        this.connection.send(message);
    };

    updateMessages = (message) => {
        this.setState({messages: this.state.messages.concat(message)});
    };

    apiUsersOnline = async (nickname) => {
        console.log(nickname);
        const response = await fetch('/users-online?nickname=' + nickname);
        const body = await response.json();
        if (response.status !== 200) {
            throw Error(body.message)
        }
        // return body;
    };

    updateUsersOnline = (users) => {
        this.setState({usersOnline: users});
    };

    render() {
        return (
            <div>
                <Login createWS={this.createWS} />
                <div>
                    <UsersOnline usersOnline={this.state.usersOnline}/>
                    <Messages messages={this.state.messages} />
                </div>
                <ChatInput sendToServer={this.sendToServer} />
            </div>
        );
    }
}

export default Chat;
// export {updateMessages};
