import React from 'react';
import openSocket from 'socket.io-client';
import {updateNav, sendToServer, updateMessages, updateUsersOnline} from "./Chat";
import Messages from "./Messages";
import ChatInput from "./ChatInput";
import '../css/styles.css';
import UserOnline from "./UserOnline";
const pathToPrivateChat = '/private/';

function connectToPrivate(partner) {
    fetch('/session/user').then(res => res.json()).then(data => {
        console.log(data);
        let conversationId = (data.user.id + '-' + partner.id);
        this.socket = openSocket(`http://localhost:5000/private?conversationId=${conversationId}`);
        this.socket.on('established', (message) => {
            console.log('Websocket: broadcast from \'private\' namespace \'established\' channel:', message);
            // this.updateUsersOnline(message.data.usersOnline);
            updateNav({
                navName: partner.nickname,
                route: pathToPrivateChat+partner.nickname
            });
            this.updateMessages(message);
        });
        this.socket.on('message', (message) => {
            console.log('Websocket: broadcast from \'private\' namespace \'message\' channel:', message);
            this.updateMessages(message);
        });
        // this.socket.on('demolished', (message) => {
        //     console.log('Websocket: broadcast from \'private\' namespace \'userDisconnected\' channel:', message);
        //     this.updateUsersOnline(message.data.usersOnline);
        //     this.updateMessages(message);
        // });
    });
}

class Private extends React.Component{

    constructor(props) {
        super(props);
        this.socket = null;
        this.state = {
            usersOnline: [],
            messages: [],
        };
        connectToPrivate = connectToPrivate.bind(this);
        // this.updateMessages = updateMessages.bind(this);
        // this.updateUsersOnline = updateUsersOnline.bind(this);
        // this.sendToServer = sendToServer.bind(this);
    }


    componentDidMount() {
        const params = new URLSearchParams(this.props.location.search);
        this.partner = {
            id: params.get('id'),
            nickname: params.get('nickname')
        };
        document.title = 'Private with ' + this.partner.nickname;
        // this.connectToPrivate();
    }



    sendToServer(message) {
        this.socket.emit('message', message);
    }

    updateMessages(message) {
        this.setState({messages: this.state.messages.concat(message)});
    }

    updateUsersOnline(users) {
        this.setState({usersOnline: users});
    }

    render() {
        console.log('PRIV', this.state.messages);
        const usersOnline = this.state.usersOnline.map((user, i) => {
            return <UserOnline key={i} user={user} path={this.props.pathToPrivateChat}
                               conversationId={this.conversationId}
                               connectToPrivate={this.connectToPrivate} />
        });
        return (
            <div>
                ALLLLLLLLLLLLOOOOOOOOOOO
                <div className='usersOnlineStyle'>
                    {usersOnline}
                </div>
                <Messages messages={this.state.messages} />
                <ChatInput sendToServer={this.sendToServer} />
            </div>
        )
    }
}

export default Private;
export {pathToPrivateChat, connectToPrivate};
