import React from 'react';
import Messages from "./Messages";
import ChatInput from "./ChatInput";
import Login from './Login';
import openSocket from 'socket.io-client';
import UserOnline from "./UserOnline";
import {BrowserRouter as Router, Route, Switch, Redirect, Link} from "react-router-dom";
import Private from './Private';
import Room from "./Room";

function updateNav(nav) {
    this.setState({nav: this.state.nav.concat(nav)})
}

class Chat extends React.Component{

    constructor(props) {
        super(props);
        this.pathToPrivateChat = '/private/';
        this.state = {
            nav: []
        };
        updateNav = updateNav.bind(this);
    }

    sendToServer = (message) => {
        this.socket.emit('message', message);
    };

    updateMessages = (message) => {
        this.setState({messages: this.state.messages.concat(message)});
    };

    updateUsersOnline = (users) => {
        this.setState({usersOnline: users});
    };

    render() {
        const nav = this.state.nav.map((item, i) => {
            return <Link to={item.route} className="nav-item nav-link" key={i}>{item.navName}</Link>
        });
        return(
            <div>
                <nav className="navbar navbar-expand-lg navbar-light bg-light">
                    <a className="navbar-brand" href="#">
                        <h3>Chat</h3>
                    </a>
                    <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavAltMarkup"
                            aria-controls="navbarNavAltMarkup" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon" />
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNavAltMarkup">
                        <div className="navbar-nav">
                            {nav}
                        </div>
                    </div>
                </nav>
                <Router>
                    <Switch>
                        <Route path={`${this.pathToPrivateChat}:id`}
                               render={(props) => <Private {...props} />}/>
                        <Redirect from={'/:path'} to={'/'} />
                        <Route path={'/'} render={
                            (props) => <Room {...props}
                                            sendToServer={this.sendToServer}
                                            updateMessages={this.updateMessages}
                                            updateUsersOnline={this.updateUsersOnline}/>
                        } />
                    </Switch>
                </Router>
            </div>
        );
    }
}

export default Chat;
export {updateNav}
