import React from 'react';

class UserOnline extends React.Component{

    constructor(props) {
        super(props);
        this.aRef = React.createRef();
        this.state.spanBackground = 'white';
        this.state = {
            toggleMenu: false
        }
    }

    componentDidMount() {
        document.addEventListener('click', this.handleOutsideClick);
    }

    toggleMenu = () => {
        this.setState({toggleMenu: !this.state.toggleMenu});
    };


    handleOutsideClick = (event) => {
        if ((this.aRef.current !== event.target) && (this.aRef.current !== event.target.parentElement)){
            this.setState({toggleMenu: false});
        }
    };

    handleMouseEnter = () => {
        this.setState({backgroundColor: 'black'})
    };

    render() {

        const divStyle = {
            position: 'absolute',
            display: this.state.toggleMenu ? 'block': 'none',
            border: 'black 1px solid',
            minWidth: '160px',
            boxShadow: '0px 8px 16px 0px rgba(0,0,0,0.2)',
            backgroundColor: 'white',
            zIndex: 1
        };

        return (
            <a ref={this.aRef} onClick={this.toggleMenu}>
                <img src={this.props.user.avatar} style={{cursor: 'pointer'}} onClick={this.toggleMenu} />{this.props.user.nickname}
                <div style={divStyle}>
                    <span style={{backgroundColor: this.state.spanBackground}} onMouseEnter={this.handleMouseEnter}>Написать в ЛС</span>
                    <span>TEST</span>
                </div>
            </a>
        )
    }
}

export default UserOnline;
