import React, { Component } from 'react';
import {updateMessages} from "./ChatInput";

class Messages extends Component {

    constructor(props){
        super(props);
        this.state = {message: 'initial'};
        updateMessages = updateMessages.bind(this);
    }

    createTable = () => {
        let html = null;
        for (let i = 0; i < this.state.msgArr.length; i++) {
            html += <p>{this.state.msgArr[i]}</p>;
        }
        return html;
    };

    render() {
        return (<h1>{this.createTable()}</h1>);
    }

}

export default Messages;
