import React, { Component } from 'react';
import '../css/styles.css';
// import {updateMessages} from "./Chat";

let updateMessages = (message) => {
    console.log(message);
    this.setState({message: this.state.message.concat(<h2>{message}</h2>)});
};

class Messages extends Component {

    constructor(props){
        super(props);
        this.state = {message: []}
        updateMessages = updateMessages.bind(this);
    }

    render() {
        return (<div className='flex-centered'><h1>{this.state.message}</h1></div>);
    }

}

export default Messages;
export {updateMessages};
