import React, { Component } from 'react';

function updateMessages(message) {
    this.setState({message: this.state.message.concat(<h2>{message}</h2>)});
}

class Messages extends Component {

    constructor(props){
        super(props);
        this.state = {message: []};
    }

    divStyle = {
        display: 'flex',
        justifyContent: 'center'
    };

    render() {
        return (<div style={this.divStyle}><h1>{this.state.message}</h1></div>);
    }

}

export default Messages;
export {updateMessages};
