import React from 'react';
import UserMenu from './UserMenu';

class UsersOnline extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            toggleUserMenu: false
        }
    }


    showUserMenu = () => {
        this.setState({toggleUserMenu: true})
    };

    render() {
        const usersOnlineStyle = {
            float: 'right',
            width: '400px',
            height: '600px',
            border: 'black 2px solid',
            padding: '20px'
        };
        const usersOnline = this.props.usersOnline.map((user, i) => {
            return (
                <div key={i}>
                    <img src={user.avatar} style={{cursor: 'pointer'}} onClick={this.showUserMenu}/>{user.nickname}
                </div>
            )
        });
        console.log(this.state.toggleUserMenu)
        return (
            <div style={usersOnlineStyle}>
                {usersOnline}
                <UserMenu toggle={this.state.toggleUserMenu} />
            </div>
        )
    }
}

export default UsersOnline;
